mapper.select:


    and code = #{code}
    and company_id = #{companyId}
    and p_code = #{pCode}

limit 1



    and code like #{code}
    and company_id = #{companyId}
    and p_code = #{pCode}

limit 1

mapper.update:
update t_code
tenant_id = #{tenantId}, status = #{status}, code = #{code}, code_acc = #{codeAcc}, code_type = #{codeType}, p_code = #{pCode}, code_attr_id = #{codeAttrId}, storage_type = #{storageType}, storage_record_id = #{storageRecordId},
where company_id=#{companyId} and code_index = #{codeIndex}

update t_code
tenant_id = #{tenantId}, storage_type = #{storageType}, storage_record_id = #{storageRecordId},
where company_id=#{companyId} and p_code = #{pCode}

update t_code
tenant_id = #{tenantId}, storage_type = #{storageType}, storage_record_id = #{storageRecordId},
where company_id=#{companyId} and code = #{code}

update t_code_${companyId}

    status = #{status},

where  company_id = #{companyId} and
code_attr_id in

    #{codeAttrId}

mapper.select:
select code_index,company_id,status,code,code_acc,code_type,p_code,code_attr_id from t_code

    and company_id = #{companyId}
    and( code = #{code} or p_code=#{code})
    and code_index >= #{indexBegin}
    and code_index <= #{indexEnd}

order by code_index

SELECT ocf.create_time as out_time, min(icf.create_time) as in_time,CONCAT(#{preFixUrl},ocf.code) as code,c.code_type, case when c.p_code is null then "" else CONCAT(#{preFixUrl},c.p_code) end as p_code,sd.dept_no, sd.dept_name,p.product_no,p.product_name,p.bar_code,so.product_id FROM `t_out_code_flow_${companyId}` AS ocf left join t_code_${companyId} c on c.`code`=ocf.code LEFT JOIN t_in_code_flow_${companyId} icf ON icf.code = ocf.code left join t_storage_out so on ocf.storage_record_id=so.id left join sys_dept sd on sd.dept_id=so.storage_to left join t_product p on p.id=so.product_id
and ocf.create_time >= #{beginTime} and ocf.create_time <= #{endTime}
group by ocf.code

select id, company_id, product_id, attr_name_cn, attr_name_en, attr_value, sort, create_user, create_time, update_user, update_time
from t_product_attr
where product_id = #{id}

select storage_record_id from t_code_${companyId}
where  storage_record_id is not null and
code_attr_id in

    #{codeAttrId}
