mapper.select:


    and r.company_id = #{companyId}
    and r.status = #{status}
    and r.type = #{type}
    and r.is_acc = #{isAcc}
    and r.tray_count = #{trayCount}
    and r.box_count = #{boxCount}
    and r.single_count = #{singleCount}
    and r.count = #{count}
    and r.index_start = #{indexStart}
    and r.index_end = #{indexEnd}
    and r.product_id = #{productId}
    and r.batch_id = #{batchId}
    and r.create_user = #{createUser}
    and r.update_user = #{updateUser}
    and r.index_start <= #{params.codeIndex}
        and r.index_end >= #{params.codeIndex}


order by r.index_end desc

mapper.update:
update t_code_record
company_id = #{companyId}, status = #{status}, type = #{type}, is_acc = #{isAcc}, tray_count = #{trayCount}, box_count = #{boxCount}, count = #{count}, index_start = #{indexStart}, index_end = #{indexEnd}, product_id = #{productId}, batch_id = #{batchId}, remark = #{remark}, create_user = #{createUser}, create_time = #{createTime}, update_user = #{updateUser}, update_time = #{updateTime},
where id = #{id}

update t_code_record
index_start = #{indexStart}, index_end = #{indexEnd},
where id = #{id}

mapper.select:
select IFNULL( SUM(count), 0 ) from  t_code_record where 1=1

    and  company_id = #{deptId}


    and create_time >= #{beginTime} and create_time <=  #{endTime}

select.if:
SELECT DATE_FORMAT( mycreatedate, '%m-%d' ) AS countKey, ifnull( sum( pv.count ), 0 ) AS countValue FROM ( SELECT curdate( ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 1 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 2 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 3 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 4 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 5 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 6 DAY ) AS mycreatedate ) AS total LEFT JOIN t_code_record pv ON DATE_FORMAT( pv.create_time, '%Y-%m-%d' ) = total.mycreatedate
and pv.company_id = #{deptId}
GROUP BY mycreatedate

