mapper.select:


     and mr.id = #{id}
     and mr.company_id = #{companyId}
     and mr.tenant_id = #{tenantId}
     and mr.code = #{code}
     and mr.mix_type = #{mixType}
     and mr.mix_from = #{mixFrom}
     and mr.product_id = #{productId}
     and mr.batch_id = #{batchId}
     and mr.province_old = #{provinceOld}
     and mr.city_old = #{cityOld}
     and mr.area_old = #{areaOld}
     and mr.province_mix = #{provinceMix}
     and mr.city_mix = #{cityMix}
     and mr.area_mix = #{areaMix}
     and mr.create_user = #{createUser}
     and mr.update_user = #{updateUser}


        and p.product_name like #{productNameLike}



        and p.product_no like #{product_noLike}


order by mr.create_time desc

mapper.update:
update t_mix_record
company_id = #{companyId}, tenant_id = #{tenantId}, code = #{code}, mix_type = #{mixType}, mix_from = #{mixFrom}, product_id = #{productId}, batch_id = #{batchId}, province_old = #{provinceOld}, city_old = #{cityOld}, area_old = #{areaOld}, province_mix = #{provinceMix}, city_mix = #{cityMix}, area_mix = #{areaMix}, create_user = #{createUser}, create_time = #{createTime}, update_user = #{updateUser}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select
mr.code,
case when mr.mix_type = 1 then '已窜货'
when mr.mix_type = 0 then '未窜货' else '' end as mixType,
mr.area_old as areaOld,
mr.area_mix as areaMix,
p.product_name as productName,
p.product_no as productNo,
pb.batch_no as batchNo,
sd.dept_name as tenantName,
sd2.dept_name as companyName
from t_mix_record mr
left join t_product p on p.id=mr.product_id
left join t_product_batch pb on pb.id=mr.batch_id
left join sys_dept sd on sd.dept_id=mr.tenant_id
left join sys_dept sd2 on sd2.dept_id=sd.parent_id
left join sys_user su on su.user_id=mr.create_user

     and mr.id = #{id}
     and mr.company_id = #{companyId}
     and mr.tenant_id = #{tenantId}
     and mr.code = #{code}
     and mr.mix_type = #{mixType}
     and mr.mix_from = #{mixFrom}
     and mr.product_id = #{productId}
     and mr.batch_id = #{batchId}
     and mr.province_old = #{provinceOld}
     and mr.city_old = #{cityOld}
     and mr.area_old = #{areaOld}
     and mr.province_mix = #{provinceMix}
     and mr.city_mix = #{cityMix}
     and mr.area_mix = #{areaMix}
     and mr.create_user = #{createUser}
     and mr.update_user = #{updateUser}


        and p.product_name like #{productNameLike}



        and p.product_no like #{product_noLike}


order by mr.create_time desc
