mapper.select:
select IFNULL(sum(code),0) from
(
    select IFNULL(sum(count),0) code from t_code_single where 1=1

        and company_id  = #{deptId}

    union all
    select IFNULL(sum(count),0) code from t_code_record where 1=1

        and company_id  = #{deptId}

    union all
    select IFNULL(sum(count),0) code from t_code_acc_record where 1=1

        and company_id  = #{deptId}

) sum

SELECT dt.date,IFNULL(t.count,0) AS count from (SELECT @s := @s + 1 AS day, DATE( DATE_SUB( CURRENT_DATE, INTERVAL @s DAY ) ) AS date FROM gen_table_column, ( SELECT @s := - 1 ) temp WHERE @s < #{day} - 1) dt left join (SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime, count( * ) as count FROM t_scan_record t WHERE 1=1
and company_id = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )) t on dt.date = t.daytime ORDER BY dt.date asc

select max(DiffDate) from
    (
        select IFNULL(DATEDIFF( max( create_time ), min( create_time ) ) + 1,0) AS DiffDate  from t_code_single where 1=1

            and company_id  = #{deptId}

        union all
        select IFNULL(DATEDIFF( max( create_time ), min( create_time ) ) + 1,0) AS DiffDate  from t_code_record where 1=1

            and company_id  = #{deptId}

        union all
        select IFNULL(DATEDIFF( max( create_time ), min( create_time ) ) + 1,0) AS DiffDate  from t_code_acc_record where 1=1

            and company_id  = #{deptId}

    ) total

SELECT DATE_FORMAT( mycreatedate, '%m-%d' ) AS date, ifnull( sum( t1.count ), 0 ) AS count FROM ( SELECT curdate( ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 1 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 2 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 3 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 4 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 5 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 6 DAY ) AS mycreatedate ) AS total LEFT JOIN t_code_record t1 ON DATE_FORMAT( t1.create_time, '%Y-%m-%d' ) = total.mycreatedate
and t1.company_id = #{deptId}
GROUP BY mycreatedate

SELECT DATE_FORMAT( mycreatedate, '%m-%d' ) AS date, ifnull( sum( t1.count ), 0 ) AS count FROM ( SELECT curdate( ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 1 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 2 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 3 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 4 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 5 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 6 DAY ) AS mycreatedate ) AS total LEFT JOIN t_code_single t1 ON DATE_FORMAT( t1.create_time, '%Y-%m-%d' ) = total.mycreatedate
and t1.company_id = #{deptId}
GROUP BY mycreatedate

SELECT DATE_FORMAT( mycreatedate, '%m-%d' ) AS date, ifnull( sum( t1.count ), 0 ) AS count FROM ( SELECT curdate( ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 1 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 2 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 3 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 4 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 5 DAY ) AS mycreatedate UNION ALL SELECT date_sub( curdate( ), INTERVAL 6 DAY ) AS mycreatedate ) AS total LEFT JOIN t_code_acc_record t1 ON DATE_FORMAT( t1.create_time, '%Y-%m-%d' ) = total.mycreatedate
and t1.company_id = #{deptId}
GROUP BY mycreatedate

SELECT city as name, count( city ) as count FROM t_scan_record where 1=1
and company_id = #{deptId}
GROUP BY city HAVING COUNT( city ) >0

SELECT province as name, count( province ) as count FROM t_scan_record where 1=1
and company_id = #{deptId}
GROUP BY province HAVING COUNT( province ) >0

SELECT HOUR ( e.create_time ) + 1 AS name, count( * ) AS count FROM t_scan_record e WHERE DATE_FORMAT(e.create_time,'%Y-%m-%d') = #{yesterday}
and company_id = #{deptId}
GROUP BY HOUR ( e.create_time ) ORDER BY HOUR ( e.create_time )

SELECT
dt.date,
IFNULL( t2.count, 0 ) AS countRecord,
IFNULL( t3.count, 0 ) AS countSingle,
IFNULL( t4.count, 0 ) AS countACC,
IFNULL( t1.count, 0 ) AS countScan,
IFNULL( t5.count, 0 ) AS countSecurity,
(IFNULL( t2.count, 0 ) +IFNULL( t3.count, 0 )+ IFNULL( t4.count, 0 ) ) AS countGenerate
FROM
(
SELECT
@myDate := @myDate + 1 AS DAY,
DATE( DATE_ADD( #{startTime}, INTERVAL @myDate DAY ) ) AS date
FROM
gen_table_column,
( SELECT @myDate := - 1 ) temp
WHERE
@myDate < #{between}
) dt
LEFT JOIN
(	SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime,count( * ) AS count FROM t_scan_record WHERE 1=1 and company_id  = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )
) t1 ON dt.date = t1.daytime
LEFT JOIN
(	SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime,IFNULL(sum(count),0) AS count FROM t_code_record WHERE 1=1 and company_id  = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )
) t2 ON dt.date = t2.daytime
LEFT JOIN
(	SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime,IFNULL(sum(count),0) AS count FROM t_code_single WHERE 1=1 and company_id  = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )
) t3 ON dt.date = t3.daytime
LEFT JOIN
(	SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime,IFNULL(sum(count),0) AS count FROM t_code_acc_record WHERE 1=1 and company_id  = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )
) t4 ON dt.date = t4.daytime
LEFT JOIN
(	SELECT DATE_FORMAT( create_time, '%Y-%m-%d' ) AS daytime,count(*) AS count FROM t_security_code_record WHERE 1=1 and company_id  = #{deptId}
GROUP BY DATE_FORMAT( create_time, '%Y-%m-%d' )
) t5 ON dt.date = t5.daytime

ORDER BY
dt.date ASC
