parent_id = #{parentId}, dept_type = #{deptType}, dept_no = #{deptNo}, dept_name = #{deptName}, ancestors = #{ancestors}, order_num = #{orderNum}, leader = #{leader}, phone = #{phone}, email = #{email}, status = #{status}, update_by = #{updateBy}, update_time = sysdate()
