mapper.select:
select count(dept_id) from sys_dept where status='0' and del_flag='0'

	and ancestors like concat('%', #{deptId}, '%')


	and create_time >= #{beginTime} and create_time <=  #{endTime}


	and create_time >= #{beginTime} and create_time <=  #{endTime}


	and create_time >= #{beginTime} and create_time <=  #{endTime}






		and parent_id = #{topdeptId}

SELECT case when d.dept_type = 1 then '直属' when d.dept_type = 2 then '自营' else '' end as deptType, (select dept_name from sys_dept where dept_id = d.parent_id) as parentName, d.dept_no as deptNo, d.dept_name as deptName, d.leader, d.phone, d.email, case when d.STATUS = 0 then '正常' when d.STATUS = 1 then '异常' else '' end as status FROM sys_dept d WHERE d.del_flag = '0'
AND parent_id = #{parentId}
AND dept_no like concat('%', #{deptNo}, '%')
AND dept_name like concat('%', #{deptName}, '%')
AND status = #{status}
${params.dataScope} order by d.parent_id, d.order_num
